mapper.select:
SELECT COUNT(*) FROM HOTDEAL_VOTE
WHERE HOTDEAL_ID = #{hotdealId}
  AND USERNAME = #{username}
  AND VOTE_TYPE = #{voteType}

mapper.delete:
DELETE FROM HOTDEAL_VOTE
WHERE HOTDEAL_ID = #{hotdealId}
  AND USERNAME = #{username}
  AND VOTE_TYPE = #{voteType}

  DELETE FROM HOTDEAL_VOTE
	WHERE HOTDEAL_ID = #{hotdealId}
  AND USERNAME = #{username}
  AND VOTE_TYPE = #{voteType}
